package day1;




public class PrimitiveDataTypeDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		
		byte test=127;
		
		System.out.println("byte data type demo "+test);
		
		
		short shortademo= 32767;
		
		System.out.println("short data type demo "+shortademo);
		
		
		int x = 2147483647;
		System.out.println("int data type demo "+x);
		
		
		long xyxlongdemo = 99999999999999999L;
		
		System.out.println("long data type demo "+xyxlongdemo);
		
		
		double d1 = 1000.234567666666;
		System.out.println("double data type demo "+d1);
		
		float f1 = 1000.2345678888f;
		System.out.println("float data type demo "+f1);
		
		
		char letter='/';
		System.out.println("char data type demo "+ letter);
		
		
		boolean testboolean=true;
		
		System.out.println("boolean data type demo "+ testboolean);
		
		
		
		String name="Computer";
				
				
		System.out.println("String data type demo "+ name);	
		
		
		int[] demoArray = {10,20,30,40,50};  //array demo
		
		System.out.println(demoArray[0]);
		
		System.out.println(demoArray[4]);
		
	}

}
