package inheritanceExample;

public class Lab2Q1 {
	
	
	int x=100;
	boolean flag = true;
	char ch = 'A';
	float y=10.5f;
	
	
	public void show(){
		System.out.println("Value of integer type x "+x);
		System.out.println("Value of boolean type flag "+flag);
		System.out.println("Value of char type ch "+ch);
		System.out.println("Value of float type  "+y);
	}
	
	
	
	
	

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Lab2Q1 obj = new Lab2Q1();
		obj.show();
		
		

	}

}
