package labexercises;

public class AssignmentDemo {
	
	

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int x=100;
		int y=3;
		
		
		System.out.println("given number x "+x);
		System.out.println("given number y "+y);
		System.out.println("Sum of given numbers "+(x+y));
		System.out.println("Difference of given numbers "+(x-y));
		System.out.println("Product of given numbers "+(x*y));
		System.out.println("Division of given numbers (quotient) "+(x/y));
		System.out.println("Division of given numbers (remainder) "+(x%y));

	}

}
