package sampleDemo;

public class WAP_PositiveOrNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int numb = 0;

		if (numb == 0) {
			System.out.println(numb + " Number is Zero");
		}
		else if (numb > 0) {
			System.out.println(numb + " Number is Positive");
		}
		else {
			System.out.println(numb + " Nummber is Negative");
		}

	}

}
